import StarRatings from "react-star-ratings";
import React,{useState} from 'react'
import _ from 'lodash' 

var ratings={}
const StarRating = (props) => {
  const [rate, setRate] = useState(0)
  async function handleSubmit(){
    try {
        console.log(props.ratings);
        const val = {
            username: window.sessionStorage.getItem('username'),
            review:props.ratings
        }
        const data = []
        const { data: res } = await axios.post('feedback/review', val)
        } catch (error) {
            if (error.response && error.response.status >= 400 && error.response.status <= 500) {
                setError(error.response.data.message)
            }
       }
    }
    const handleRating =  (newRating, name) => {
        name = name.split('+')
      _.set(ratings, [name[0], name[1]], newRating)
      setRate(newRating)
      props.onChange(ratings)
      console.log(ratings);
      }
  return (
          <StarRatings
            rating={rate}
            numberOfStars={5}
            changeRating={handleRating}
            starDimension={30}
            starSpacing={0}
            starRatedColor={'rgb(253, 236, 1)'}
            starEmptyColor={'rgb(109, 122, 130)'}
            starHoverColor={'rgb(253, 1, 1)'}
            name={props.label}
          />
  )
}
export {StarRating}
