import React,{useState} from 'react'
import {Button} from 'react-bootstrap'
import axios from 'axios';

export const AddCourse = () => {
    const [response, setResponse] = useState('')
    const handleAddCourse = async () =>{
    var co = (document.getElementById('co_input').value).split('\n')
    for (let i = 0; i < co.length; i++) {
        if (co[i].includes('\n'))
            co[i]=co[i].split('\n')[0]
        }
    co.splice()
    console.log(co);
    // const { data: res } = await axios.post('addFeedBack')
    // setResponse(res.message)
    }
  return (
    <div style={{backgroundColor:'',width:'100%',height:'100%'}}>
                  <table style={{
                    width:'100%'  
                  }}>
                    <tbody style={{width:'100%'}}>
                    <tr class='tdVal' style={{display: 'flex'}}>
                      <td style={{flex:'1'}}><label>Course Name:</label></td>
                        <td style={{ flex: '1' }}><label></label></td>
                    </tr>
                    <tr class='tdVal' style={{display: 'flex'}}>
                      <td style={{flex:'1'}}><label>Course Id:</label></td>
                        <td style={{ flex: '1' }}><label></label></td>
                    </tr>
                    <tr class='tdVal' style={{display: 'flex',width:'100%'}}>
                      <td style={{flex:'1'}}><label>Academic Year:</label></td>
                      <td style={{flex:'1'}}></td>
                    </tr>
                    <tr class='tdVal' style={{display: 'flex'}}>
                      <td style={{flex:'1'}}><label>Semester:</label></td>
                      <td style={{flex:'1'}}></td>
                    </tr>
                    <tr class='tdVal' style={{display: 'flex'}}>
                      <td style={{flex:'1'}}><label>Enter CO's:</label></td>
                      <td style={{flex:'1'}}><textarea id='co_input' rows="5" cols="105" placeholder="This is the default text"></textarea></td>
                    </tr>
                    </tbody>
                    <Button className='btn btn-success' onClick={handleAddCourse}>Send for Feedback</Button> <p>{  }</p>
                  </table>
                </div>
  )
}
