import React,{useState,useEffect} from 'react'
import { Radar } from 'react-chartjs-2';
import {
    Chart as ChartJS,
    RadialLinearScale,
    PointElement,
    LineElement,
    Filler,
    Tooltip,
    Legend,
  } from 'chart.js';
import axios from 'axios';
ChartJS.register(
    RadialLinearScale,
    PointElement,
    LineElement,
    Filler,
    Tooltip,
    Legend
    );
export const RadarChart = (props) => {
    const [ratings, setRatings] = useState({})
    useEffect(() => {
        const getRatings = async () => {
            const data = { 'username': '20BAD036', 'course': 'PYTHON PROGRAMMING' }
            const res = await axios.post('getRatings', data)
            setRatings(res.data)
            // console.log(res.data)
        }
        getRatings()
    }, [ratings])
    const d = {
        labels: ['Thing 1', 'Thing 2', 'Thing 3', 'Thing 4', 5],
        datasets: [
            {
                label: '# of Votes',
                data: ratings,
                backgroundColor: 'rgba(255, 99, 132, 0.2)',
                borderColor: 'rgba(255, 99, 132, 1)',
                borderWidth: 1,
                fill: true,
                backgroundColor: "rgba(255,99,132,0.2)",
                borderColor: "rgba(255,99,132,1)",
                pointBorderColor: "#fff",
                pointBackgroundColor: "rgba(255,99,132,1)"
            }]
    }
    var        options: {
            legend: {
                display: false
            },
            borderWidth: 10,
            scale: {
                ticks: {
                    fontSize: 18,
                    max: 100
                },
                gridLines: {
                    lineWidth: 2,
                    color: "lightgreen"
                },
                pointLabels: {
                    fontSize: 18,
                    fontStyle: "bold"
                }
            }
        }
    return <Radar data={d} />
}
