import React,{useState,useEffect} from 'react'
import { Radar } from 'react-chartjs-2';
import {
    Chart as ChartJS,
    RadialLinearScale,
    PointElement,
    LineElement,
    Filler,
    Tooltip,
    Legend,
  } from 'chart.js';
import axios from 'axios';
ChartJS.register(
    RadialLinearScale,
    PointElement,
    LineElement,
    Filler,
    Tooltip,
    Legend
    );
export const RadarChart = (props) => {
    const [data, setData] = useState({ 'username': '20BAD036', 'course':'PYTHON PROGRAMMING'})
    const [ratings, setRatings] = useState({})
    useEffect(() => {
        const getRatings = async () => {
            const res = await axios.post('getRatings',data)
            setRatings(res.data)
        }
        getRatings()
    },)
    
    return <Radar data={ratings} />
}
