import axios from 'axios';
import React,{useState} from 'react'
import { NavBar } from '../components/NavBar'
import { Banner } from '../components/banner'
import {Button} from 'react-bootstrap'
import { DynDropDown } from '../components/dynDropDown'
export const StudentDashboard = () => {
    const [academicYear, setAcademicYear] = useState('')
    const [semester, setSemester] = useState('')
    const [courseDetails, setCourseDetails] = useState('')
    const [response, setResponse] = useState('')
    const sendFeedback = async () => {
        var data = {
            facultyUserName: window.sessionStorage.getItem('username'),
            academicYear: academicYear,
            semester: semester,
            courseId: courseDetails.split('-')[1],
            courseName: courseDetails.split('-')[0],
        }
        const { data: res } = await axios.post('addFeedBack', data)
        setResponse(res.message)
    }
    return (
        <div>
            <div>
                <NavBar />
                <Banner />
                <div style={{ backgroundColor: '',height:'100%',width:'100%' }}>
                    <h5 style={{
                        textAlign: 'center',
                        margin: '20px',
                        textDecoration: 'overline underline black solid 2px',
                        zoom: '150%',
                        fontFamily: 'Lucida Handwriting',
                    }}>
                        Student's DashBoard
                    </h5>
                </div>
            </div>

        </div>
    )
}
