import React,{useState,useEffect} from 'react'
import { Radar } from 'react-chartjs-2';
import {
    Chart as ChartJS,
    RadialLinearScale,
    PointElement,
    LineElement,
    Filler,
    Tooltip,
    Legend,
  } from 'chart.js';
import axios from 'axios';
ChartJS.register(
    RadialLinearScale,
    PointElement,
    LineElement,
    Filler,
    Tooltip,
    Legend
    );
export const RadarChart = (props) => {
    const [ratings, setRatings] = useState({})
    const [d,setD] = useState({})
    useEffect(() => {
        const getRatings = async () => {
            const data = { 'username': window.sessionStorage.getItem('username'), 'course': props.courseName}
            const res = await axios.post('getRatings',data)
            setRatings(res.data)
        }
        getRatings()
         setD({
            labels: ['Thing 1', 'Thing 2', 'Thing 3', 'Thing 4'],
            datasets: [
              {
                label: '# of Votes',
                data: ratings,
                backgroundColor: 'rgba(255, 99, 132, 0.2)',
                borderColor: 'rgba(255, 99, 132, 1)',
                borderWidth: 1,
              },
            ],
          })
    },[props.courseName, ratings])
    return <Radar data={d} />
}
